mapper.insert:

insert into cms_department (parent_id, id_path, dept_name,
  dept_code, sort, leader, 
  mobile, `status`, remark, 
  create_id, create_time, update_time
  )
values (#{parentId,jdbcType=BIGINT}, #{idPath,jdbcType=VARCHAR}, #{deptName,jdbcType=VARCHAR}, 
  #{deptCode,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}, #{leader,jdbcType=VARCHAR}, 
  #{mobile,jdbcType=VARCHAR}, #{status,jdbcType=CHAR}, #{remark,jdbcType=VARCHAR}, 
  #{createId,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select *
from cms_department


    and id = #{id}


    and parent_id = #{parentId}


    and id_path like concat(#{idPath},'%')


    and dept_name like concat('%',#{deptName},'%')


    and dept_code like concat('%',#{deptCode},'%')


    and `status` = #{status}


order by sort
