mapper.insert:

insert into cms_role (role_code, role_name, `status`, 
  sort, data_scope, remark, 
  create_id, create_time, update_time
  )
values (#{roleCode,jdbcType=VARCHAR}, #{roleName,jdbcType=VARCHAR}, #{status,jdbcType=CHAR}, 
  #{sort,jdbcType=INTEGER}, #{dataScope,jdbcType=TINYINT}, #{remark,jdbcType=VARCHAR}, 
  #{createId,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select *
from cms_role


    and role_code = #{roleCode}


    and role_name like concat('%', #{roleName}, '%')


    and `status` = #{status}


order by sort
